import "./Header.css";
import { Button, ButtonGroup, Typography } from "@mui/material";
import { myStore } from "../../../redux/Store";
import { checkData } from "../../../util/chekData";
import { useEffect, useState } from "react";
import { logoutAction } from "../../../redux/authReducer";
import { useNavigate } from "react-router-dom";
import { CenterFocusStrong } from "@mui/icons-material";


export function Header(): JSX.Element {
    const [isLogged, setLogged] = useState(false);
    const [userName, setName] = useState("");

    useEffect(() => {
        checkData();
    }, [])
    myStore.subscribe(() => {
        setName(myStore.getState().auth.name);
        setLogged(myStore.getState().auth.isLogged);
    })

    const navigate = useNavigate();
    return (
        <div className="Header">
            <div>
                <Typography variant="h3" className="HeadLine" alignSelf={CenterFocusStrong}>Coupons Website</Typography>
            </div>
            <div className="login">
                Hello {userName} <br />
                <ButtonGroup variant="contained">
                    <Button type="submit" color={isLogged ? "secondary" : "info"}
                        onClick={() => {
                            if (isLogged) {
                                sessionStorage.removeItem("jwt");
                                myStore.dispatch(logoutAction());
                                navigate("/")
                            } else {
                                navigate("/Login")
                            }
                        }}>{isLogged ? "Logout" : "Login"}</Button>
                    {!isLogged && <Button value="Register" color="secondary" onClick={() => navigate("/register")}>Register</Button>};
                </ButtonGroup>
            </div>
        </div>
    );
}
